mapper.select:
select
	coalesce(max(question_num) + 1,1) as question_num
from
	batch_exam_listening_question
where
	type = #{type}
	and num = #{num}
	and sub_num = #{sub_num}
	and article_num = #{article_num}

mapper.delete:
delete from batch_exam_listening_question
where
	type = #{type}
	and num = #{num}
	and sub_num = #{sub_num}
	and article_num = #{article_num}
	and question_num = #{question_num}
