mapper.select:
select count(*) as total_count
from 
	internal_exams_chain 
where 1 = 1

	and section = #{section}


	and book = #{book}


	and volume = #{volume}

select a.id, a.section, a.book, a.volume, a."group", a.article, a.netpg_book, a.netpg_jindo from internal_exams_chain a left join ( select a.section, a.section_order, b.book, b.book_order, c.volume, c.volume_order, coalesce(d.group,'') as group, coalesce(d.group_order,0) as group_order from practices_practicesection a join practices_practicebook b on a.id = b.practice_section_id join practices_practicevolume c on b.practice_section_id = c.practice_section_id and b.id = c.practice_book_id left join practices_practicegroup d on c.practice_section_id = d.practice_section_id and c.practice_book_id = d.practice_book_id and c.id = d.practice_volume_id ) b on a.section = b.section and a.book = b.book and a.volume = b.volume and a.group = b.group where 1 = 1
and a.section = #{section}
and a.book = #{book}
and a.volume = #{volume}
order by coalesce(b.section_order,0), a.section, coalesce(b.book_order,0), a.book, coalesce(b.volume_order,0), a.volume, coalesce(b.group_order,0), a.group, a.article limit #{row_num} offset #{first_num}

mapper.update:
update internal_exams_chain set
	modified = now(),
	contents = #{contents}
where
	id = #{id}

update internal_exams_chain set
	modified = now(),
	modifier_set = #{modifier_set},
	color_set = #{color_set}
where
	id = #{id}
