mapper.update:
update base_country1 set
	country1 = #{country1}
where
	id = #{id}

update base_country2 set
	country1 = #{country1}
where
	country1 = #{old_country1}

update users_school set
	school_area1 = #{country1}
where
	school_area1 = #{old_country1}

mapper.insert:
insert into base_country2(
	country1,
	country2
) values (
	#{country1}
)

mapper.update:
update base_country2 set
	country1 = #{country1},
	country2 = #{country2}
where
	id = #{id}

update users_school set
	school_area1 = #{country1},
	school_area2 = #{country2}
where
	school_area1 = #{old_country1}
	and school_area2 = #{old_country2}
