mapper.select:
select coalesce(max(describe_sort),0) + 1 as describe_sort 
from
	internal_exams_duolingo_describe a
where
	a.section = #{section}
	and a.book = #{book}
	and a.volume = #{volume}
	and a."group" = #{group}
	and a.article = #{article}

mapper.update:
update internal_exams_duolingo_describe set
	modified = now(),
	modified_id = #{modified_id},
	describe_sort = #{describe_sort}
where
	id = #{id}
