mapper.select:
select count(*) as total_count
from 
	internal_exams_reading
where 1 = 1

	and section = #{section}


	and book = #{book}


	and volume = #{volume}

select a.id, a.section, a.book, a.volume, a."group", a.article, a.passage from internal_exams_reading a join ( select a.section, a.section_order, b.book, b.book_order, c.volume, c.volume_order, coalesce(d.group,'') as group, coalesce(d.group_order,0) as group_order from practices_practicesection a join practices_practicebook b on a.id = b.practice_section_id join practices_practicevolume c on b.practice_section_id = c.practice_section_id and b.id = c.practice_book_id left join practices_practicegroup d on c.practice_section_id = d.practice_section_id and c.practice_book_id = d.practice_book_id and c.id = d.practice_volume_id ) c on a.section = c.section and a.book = c.book and a.volume = c.volume and a.group = c.group where 1 = 1
and a.section = #{section}
and a.book = #{book}
and a.volume = #{volume}
order by coalesce(c.section_order,0), a.section, coalesce(c.book_order,0), a.book, coalesce(c.volume_order,0), a.volume, coalesce(c.group_order,0), a.group, a.article limit #{row_num} offset #{first_num}
