mapper.select:
select
	id,
    test_type,
    student_type,
    lecture_type,
    routine_category,
    routine_schedule
from
	notices_routine_trans_coursegroup
where
	test_type = #{test_type}
    and student_type = #{student_type}
    and lecture_type = #{lecture_type}
    and routine_category = #{routine_category}
	and routine_schedule = #{routine_schedule}

select
    routine_schedule
from
	notices_routine_trans_coursegroup
where
	test_type = #{test_type}
    and student_type = #{student_type}
    and lecture_type = #{lecture_type}
    and routine_category = #{routine_category}
order by routine_schedule
